// Accessor functions to ease Fortran-JavaScript conversion:
function make_accessor(array, max) {
	if (!('length' in array))
		throw Error("Missing required array");
	return function(i) {
		if (i >= 1 && i <= max) {
			i--;
			return i < array.length ? array[i] : 0;
		}
		throw Error("Index out of bounds [1-" + max + "]: " + i);
	};
}

